"use strict";
// Null represents an intentional value abscence, it fills a space of variable that is expected to be undefined. Once defined as null it will only accept this value.
let varNull = null;
//varNull = 1
console.log(varNull); // returns null
console.log(typeof varNull); // returns object
// Undefined is used for variables not iniciated, has only one value that is undefined, function that does not return a value.
let varUndefined;
console.log(varUndefined); // returns undefined
console.log(typeof varUndefined); // returns undefined
